import React from "react";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Progressbar from "./Progressbar";
import "./QuizApi.css";

function Quizproblem({handleAnswerdata:{question,correct_answer,answers}}){

    const testData = [
        { bgcolor: "#f31303d7", completed: 10 }
      ];
      const [value, setValue] = React.useState('');
      const handleChange = (event) => {
        setValue(event.target.value);
      };
         
    return <>
                <h2 className="quiz-api-title">Quiz</h2>
                    <p>
                    {testData.map((item, idx) => (
                    <Progressbar key={idx} bgcolor={item.bgcolor} completed={item.completed} />
                    ))}
                    </p>
                    <h3 className="quiz-api-question" dangerouslySetInnerHTML={{__html:question}} />
                    {/* <label className="quiz-api-container">One
                    <input type="checkbox"  />
                    <span class="checkmark"></span>
                    </label>
                    <label class="quiz-api-container">Two
                    <input type="checkbox"   />
                    <span class="checkmark"></span>
                    </label>

                    <label class="quiz-api-container">Three
                    <input type="checkbox"   />
                    <span class="checkmark"></span>
                    </label>

                    <label class="quiz-api-container">Four
                    <input type="checkbox"  />
                    <span class="checkmark"></span>
                    </label> */}            
                    <FormControl component="fieldset">
                    <RadioGroup
                        aria-label="gender"
                        name="controlled-radio-buttons-group"
                        value={value}
                        onChange={handleChange}
                    >
                    {answers.map((answers,idx) => {
                        return (
                            <FormControlLabel value={answers} onClick= { () => handleAnswer(answers)} control={<Radio color="success" />} label={answers} />
                        )
                    })}
                    </RadioGroup>
                    </FormControl>
                    <div className="prev-next">
                    <Stack direction="row" spacing={55}>
                    <Button color="error" variant="outlined" disabled>
                        Prev
                    </Button>
                    <Button onClick={handleNext()} color="success" variant="outlined" href="#outlined-buttons">
                        Next
                    </Button>
                    </Stack>
                    </div>
    </>
}

export default Quizproblem;
