import React, {useState , useEffect} from "react";
import "./QuizApi.css";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Progressbar from "./Progressbar";
import Axios from "axios";
function Quizapi(){
    const API_URL = "https://opentdb.com/api.php?amount=10&category=18&difficulty=medium&type=multiple";
    const [questions,setquestions] = useState([]);
    const [currentIndex,setCurrentIndex] = useState(0);

    useEffect(() => {
        Axios.get(API_URL)
        .then(res => res.data)
        .then(data =>{
            setquestions(data.results)
        })
    },[])
   
   
    const testData = [
        { bgcolor: "#f31303d7", completed: 10 }
      ];
      const [value, setValue] = React.useState('');
      const handleChange = (event) => {
        setValue(event.target.value);
      };
         
return <>
            <div className="quiz-api-body">
                <div className="quiz-api-questionBlock">
                    <h2 className="quiz-api-title">Quiz</h2>
                    <p>
                    {testData.map((item, idx) => (
                    <Progressbar key={idx} bgcolor={item.bgcolor} completed={item.completed} />
                    ))}
                    </p>
                    <h3 className="quiz-api-question">{questions[currentINdex]}</h3>
                    {/* <label className="quiz-api-container">One
                    <input type="checkbox"  />
                    <span class="checkmark"></span>
                    </label>
                    <label class="quiz-api-container">Two
                    <input type="checkbox"   />
                    <span class="checkmark"></span>
                    </label>

                    <label class="quiz-api-container">Three
                    <input type="checkbox"   />
                    <span class="checkmark"></span>
                    </label>

                    <label class="quiz-api-container">Four
                    <input type="checkbox"  />
                    <span class="checkmark"></span>
                    </label> */}            
                    <FormControl component="fieldset">
                    <RadioGroup
                        aria-label="gender"
                        name="controlled-radio-buttons-group"
                        value={value}
                        onChange={handleChange}
                    >
                        <FormControlLabel value="option1" control={<Radio color="success" />} label="male" />
                        <FormControlLabel value="option2" control={<Radio color="success" />} label="Male" />
                        <FormControlLabel value="option3" control={<Radio color="success" />} label="Male" />
                        <FormControlLabel value="option4" control={<Radio color="success" />} label="Male" />
                    </RadioGroup>
                    </FormControl>
                    <div className="prev-next">
                    <Stack direction="row" spacing={55}>
                    <Button color="error" variant="outlined" disabled>
                        Prev
                    </Button>
                    <Button color="success" variant="outlined" href="#outlined-buttons">
                        Next
                    </Button>
                    </Stack>
                    </div>
                </div>
            </div>
    </>
}

   

export default Quizapi;
