            <div className="quiz-api-body">
                { questions.length > 0 ? ( <div className="quiz-api-questionBlock">
                    <Quizproblem  data={questions[currentIndex]} />
                </div>) : "Loading...." }
               
            </div>
    </>
}

   

export default Quizapi;
