import React, {useState , useEffect} from "react";
import Quizproblem from './Quizproblem';
import "./QuizApi.css";

import Axios from "axios";
function Quizapi(){
    const API_URL = "https://opentdb.com/api.php?amount=10&category=18&difficulty=medium&type=multiple";
    const [questions,setquestions] = useState([]);
    const [currentIndex,setCurrentIndex] = useState(0);
    useEffect(() => {
        Axios.get(API_URL)
        .then(res => res.data)
        .then(data =>{
            if(data.result.question){
            const problem = data.result.map((question) => ({
                ...question,
                answers:[question.correct_answer, ...question.incorrect_answers].sort(() => Math.random() - 0.5)
            }))

            setquestions(problem);
        }
        })
    },[])
   
return <>
            <div className="quiz-api-body">
                { questions.length > 0 ? ( <div className="quiz-api-questionBlock">
                    <Quizproblem  data={questions[currentIndex]} />
                </div>) : "Loading...." }
               
            </div>
    </>
}

   

export default Quizapi;
