import React from "react";
import "./QuizApi.css";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import Progressbar from "./Progressbar";
function Quizapi(){
    const testData = [
        { bgcolor: "#f31303d7", completed: 60 }
      ];
      const [value, setValue] = React.useState('female');

      const handleChange = (event) => {
        setValue(event.target.value);
      };
         
return <>
            <div className="quiz-api-body">
                <div className="quiz-api-questionBlock">
                    <h2 className="quiz-api-title">Quiz</h2>
                    <p>
                    {testData.map((item, idx) => (
                    <Progressbar key={idx} bgcolor={item.bgcolor} completed={item.completed} />
                    ))}
                    </p>
                    <h3 className="quiz-api-question">Learn how to create custom checkboxes and radio buttons with CSS.</h3>
                    {/* <label className="quiz-api-container">One
                    <input type="checkbox"  />
                    <span class="checkmark"></span>
                    </label>

                    <label class="quiz-api-container">Two
                    <input type="checkbox"   />
                    <span class="checkmark"></span>
                    </label>

                    <label class="quiz-api-container">Three
                    <input type="checkbox"   />
                    <span class="checkmark"></span>
                    </label>

                    <label class="quiz-api-container">Four
                    <input type="checkbox"  />
                    <span class="checkmark"></span>
                    </label> */}            
                    <FormControl component="fieldset">
                    <FormLabel component="legend">Gender</FormLabel>
                    <RadioGroup
                        aria-label="gender"
                        name="controlled-radio-buttons-group"
                        value={value}
                        onChange={handleChange}
                    >
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                    </RadioGroup>
                    </FormControl>
                </div>
            </div>
    </>
}

export default Quizapi;
