import React,{useState} from "react";
import {Form} from 'react-bootstrap';
import "./Register.css";
import FacebookIcon from '@mui/icons-material/Facebook';
import TwitterIcon from '@mui/icons-material/Twitter';
import GoogleIcon from '@mui/icons-material/Google';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import CampaignIcon from '@mui/icons-material/Campaign';
function Register(){
    const[user,setUser] = useState({
        Email:"",
        Password:"",
        Confirmpass:""
    })

    function handleChange(event){
        const {name,value} = event.target;
        setUser({
            ...user,
            [name] : value
        })
        
    }

    const url="";
    return <>
        <Form className="login-div">
        <h2>WELCOME TO</h2>
        <h1><CampaignIcon className="speaker" fontSize="60" />Quizz</h1>
        <h5>Register or Login to Enter the quizz world And check your knowledge </h5>
        <input onChange={handleChange} type="email" name="Email" value={user.Email}   placeholder="Email" />
        <input onChange={handleChange} type="password" name="password" value={user.Password}  placeholder="Password" /><br/>
        <input onChange={handleChange} type="password" className="confirm-pass" value={user.Confirmpass} name="confirm-pass" placeholder="Confirm Password" /><br />
        <button>Register</button>
        <h6>Already Have An Account ? <a href={url}>Login Now </a></h6>
        <hr />
        <h6>Continue With Social Media</h6>
        <ul className="socials">
        <li><FacebookIcon /></li>
        <li><TwitterIcon /></li>
        <li><GoogleIcon /></li>
        <li><LinkedInIcon /></li>
        </ul>
        </Form>
    </>
}

export default Register;
