import React, {useState , useEffect} from "react";
import Quizproblem from './Quizproblem';
import "./QuizApi.css";

import Axios from "axios";
function Quizapi(){
    const API_URL = "https://opentdb.com/api.php?amount=10&category=18&difficulty=medium&type=multiple";
    const [questions,setquestions] = useState([]);
    const [currentIndex,setCurrentIndex] = useState(0);
    const [Score,Setscore] = useState(0);
    useEffect(() => {
        Axios.get(API_URL)
        .then(res => res.data)
        .then(data =>{
            if(data.results){
            const problem = data.results.map((question) => ({
                ...question,
                answers:[question.correct_answer, ...question.incorrect_answers].sort(() => Math.random() - 0.5)
            }))

            setquestions(problem);
        }
        })
    },[])
   
    const handleAnswer = (answer) => {
            if(answer === questions[currentIndex].correct_answer){
                Setscore(Score+1);
            }
           
    }
    function handleNext(){
        setCurrentIndex(currentIndex+1);
    }
return <>   
            <div className="quiz-api-body">
              questions.length > 0 ? (
                
                {currentIndex >= questions.length ? (
                    <h1>Quiz Ended, Your Score is {Score}</h1>) : 
             ( <div className="quiz-api-questionBlock">
                    <Quizproblem  handleNext={handleNext} handleAnswer={handleAnswer}  data={questions[currentIndex]} />
                </div>
                )
                 }  
            </div> : "Loading...." ) 
    </>
}

   

export default Quizapi;
